    private void loadKey() {
        try {
            boolean b = Ped.getInstance().loadMKey(
                    (byte) 0xff, (byte)1, mainKeyDefault, 1,
                    Ped.KEY_TYPE_UNENCTYPTED_KEY, null, false
            );
            Ped.getInstance().loadWorkKeyByIdx(
                    (byte)1, Ped.WORK_KEY_TYPE_PIN_KEY, (byte)1,
                    workKeyDefault, null
            );
        } catch (SDKException e) {
            e.printStackTrace();
        } catch (PINPADException e) {
            e.printStackTrace();
        }
    }

