    /**
     * Config country code,capability,transaction currency code,transaction currency code exponent
     * <br/> Client should config their own data according their country.
     */
    public static Map<String, String> getConfig(String country) {
        Map<String, String> map = new HashMap<>();
        switch (country) {
            case COUNTRY_NIGERIA:
                map.put("countryCode", "0566");
                map.put("capability", "E0F8C8");
                map.put("5F2A", "0566");
                map.put("5F36", "00");
                break;
            default:
                map.put("countryCode", "0566");     // country code
                map.put("capability", "E0F8C8");    // capability
                map.put("5F2A", "0566");            // transaction currency code
                map.put("5F36", "00");              // transaction currency code exponent
        }
        return map;
    }

